if ('serviceWorker' in navigator) {
    navigator.serviceWorker.register('/scripts/ServiceWorker.min.js')
        .then(function () {
            // console.info("ServiceWorker został zarejestrowany pomyślnie.");
        })
        .catch(function (err) {
            console.error('Błąd podczas rejestracji ServiceWorkera:', err);
        });
}

const APP_CACHE = 'appCache';

self.addEventListener('install', function (event) {
    console.log("Service Worker: Instalacja...");
    event.waitUntil(
        caches.open(APP_CACHE).then(function (cache) {
            return cache.addAll([
                '/index.php'
            ]);
        })
    );
});

self.addEventListener('fetch', function (event) {
    console.log("Service Worker: Żądanie:", event.request.url);
    event.respondWith(
        caches.match(event.request).then(function (response) {
            return response || fetch(event.request);
        })
    );
});
